\Copy (SELECT vestiging_id, vestigingsnummer,hoofdvestiging,volledig_adres,datum_aanvang,datum_einde,naam,sbi_code,hoofdactiviteit,toevoeging_adres,postbus_nummer,bag_numid,bag_vbid,straat_huisnummer,postcode_woonplaats,regio,land,geometrie FROM hr_vestiging_activiteiten hr_a JOIN hr_vestiging vs ON hr_a.vestiging_id = vs.id JOIN hr_activiteit a ON a.id = hr_a.activiteit_id JOIN hr_locatie loc ON (vs.bezoekadres_id = loc.id OR vs.postadres_id = loc.id) AND ST_IsValid(loc.geometrie) is null WHERE volledig_adres LIKE '%Amsterdam%' ORDER BY volledig_adres) TO 'hr_ves_amsterdam.csv' WITH DELIMITER ',' CSV HEADER
